mapper.update:
UPDATE USER
SET
       refresh_token = #{rtk}
     , last_login_at = CURRENT_TIMESTAMP
     , updated_at    = CURRENT_TIMESTAMP
WHERE id = #{userId}

mapper.insert:
INSERT INTO USER_LOGIN_HISTORY (
       user_id
     , success_yn
     , ip_address
     , user_agent
     , fail_reason
     , login_at
)
VALUES (
       #{userId}
     , #{successYn}
     , #{ipAddress}
     , #{userAgent}
     , #{failReason}
     , #{loginAt}
)
