mapper.insert:
INSERT INTO GACHA (
       id
     , status
     , title
     , manufacturer
     , file_id
     , view_count
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{status}
     , #{title}
     , #{manufacturer}
     , #{fileId}
     , #{viewCount}
     , #{createdAt}
     , #{updatedAt}
)

INSERT INTO GACHA_ITEM (
       gacha_id
     , name
     , file_id
     , created_at
     , updated_at
)
VALUES (
       #{gachaId}
     , #{name}
     , #{fileId}
     , #{createdAt}
     , #{updatedAt}
)

mapper.update:
UPDATE GACHA
SET
       title        = #{newTitle}
     , manufacturer = #{newManufacturer}
WHERE id = #{gachaId}

UPDATE GACHA_ITEM
SET name = #{newName}
WHERE id = #{gachaItemId}
