mapper.insert:
INSERT INTO GACHA (
       id
     , status
     , title
     , manufacturer
     , file_id
     , price
     , view_count
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{status}
     , #{title}
     , #{manufacturer}
     , #{fileId}
     , #{price}
     , #{viewCount}
     , #{createdAt}
     , #{updatedAt}
)

INSERT INTO GACHA_ITEM (
       gacha_id
     , name
     , file_id
     , rarity
     , created_at
     , updated_at
)
VALUES (
       #{gachaId}
     , #{name}
     , #{fileId}
     , #{rarity}
     , #{createdAt}
     , #{updatedAt}
)

mapper.select:
SELECT G.id AS gachaId , G.status , G.title , G.manufacturer , G.file_id , F.file_url , G.price , G.view_count , G.created_at , G.updated_at FROM GACHA G LEFT JOIN IMAGE_FILE F ON F.file_id = G.file_id WHERE 1=1
G.title LIKE #{keywordPattern} OR G.manufacturer LIKE #{keywordPattern}
ORDER BY G.created_at ASC
