mapper.insert:
INSERT INTO IMAGE_FILE (
       file_id
     , identifier
     , original_file_name
     , stored_file_name
     , size
     , file_url
     , created_at
)
VALUES (
       #{fileId}
     , #{identifier}
     , #{originalFileName}
     , #{storedFileName}
     , #{size}
     , #{fileUrl}
     , #{createdAt}
)

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM IMAGE_FILE WHERE file_id = #{fileId}
)
