mapper.select:
SELECT COUNT(id)
FROM TRADE
WHERE gacha_id = #{gachaId}
AND   user_id  = #{userId}
AND   status   = 'PENDING'
AND   type     = 'EXCHANGE'

mapper.insert:
INSERT INTO TRADE (
       id
     , gacha_id
     , user_id
     , type
     , status
     , title
     , content
     , file_id
     , hope_method
     , hope_location
     , hope_address
     , hope_location_latitude
     , hope_location_longitude
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{gachaId}
     , #{userId}
     , #{type}
     , #{status}
     , #{title}
     , #{content}
     , #{fileId}
     , #{hopeMethod}
     , #{hopeLocation}
     , #{hopeAddress}
     , #{hopeLocationLatitude}
     , #{hopeLocationLongitude}
     , #{createdAt}
     , #{updatedAt}
)

INSERT INTO TRADE_EXCHANGE_ITEM (
       trade_id
     , gacha_id
     , give_item_id
     , want_item_id
     , created_at
     , updated_at
)
VALUES (
       #{tradeId}
     , #{gachaId}
     , #{giveItemId}
     , #{wantItemId}
     , #{createdAt}
     , #{updatedAt}
)
