mapper.select:
SELECT CASE WHEN COUNT(id) > 0 THEN 'Y' ELSE 'N' END
FROM GACHA_WISH
WHERE user_id = #{userId} AND gacha_id = #{gachaId}

mapper.insert:
INSERT INTO GACHA_WISH (
       user_id
     , gacha_id
     , created_at
)
VALUES (
       #{userId}
     , #{gachaId}
     , #{createdAt}
)

mapper.select:
SELECT COUNT(id)
FROM TRADE
WHERE  gacha_id = #{gachaId}
 AND  user_id   = #{userId}
 AND  status    = 'PENDING'
 AND  type      = 'EXCHANGE'

SELECT COUNT(id)
FROM TRADE
WHERE gacha_id   = #{gachaId}
 AND   user_id   = #{userId}
 AND   status    = 'PENDING'
 AND   type      = 'SALES'
