mapper.select:
SELECT CASE WHEN COUNT(id) > 0 THEN 'Y' ELSE 'N' END
FROM GACHA_WISH
WHERE user_id = #{userId} AND gacha_id = #{gachaId}

mapper.insert:
INSERT INTO GACHA_WISH (
       user_id
     , gacha_id
     , created_at
)
VALUES (
       #{userId}
     , #{gachaId}
     , #{createdAt}
)

mapper.select:
SELECT COUNT(id)
FROM TRADE
WHERE  gacha_id = #{gachaId}
 AND  user_id   = #{userId}
 AND  status    = 'PENDING'
 AND  type      = 'EXCHANGE'

SELECT COUNT(id)
FROM TRADE
WHERE gacha_id   = #{gachaId}
 AND   user_id   = #{userId}
 AND   status    = 'PENDING'
 AND   type      = 'SALES'

SELECT COUNT(DISTINCT T.id) FROM TRADE T INNER JOIN TRADE_ITEM TI ON TI.trade_id = T.id WHERE T.gacha_id = #{gachaId}
AND T.status = 'PENDING'
AND ( (T.type = 'SALES' AND TI.sales_item_id = #{gachaItemId}) OR (T.type = 'EXCHANGE' AND TI.exchange_give_item_id = #{gachaItemId}) )

SELECT DISTINCT T.id AS trade_id , T.gacha_id , T.user_id , U.nickname AS user_nickname , T.type , T.status , T.title , T.content , T.file_id , (SELECT file_url FROM IMAGE_FILE WHERE file_id = T.file_id ORDER BY created_at LIMIT 1) AS file_url , T.hope_method , T.hope_location , T.hope_address , T.hope_location_latitude , T.hope_location_longitude , (SELECT COUNT(id) FROM TRADE_SUGGEST WHERE trade_id = T.id AND gacha_id = T.gacha_id AND status != 'CANCELED') AS suggest_count , T.created_at FROM TRADE T INNER JOIN USER U ON U.id = T.user_id INNER JOIN TRADE_ITEM TI ON TI.trade_id = T.id WHERE T.gacha_id = #{gachaId}
AND T.status = 'PENDING'
AND ( (T.type = 'SALES' AND TI.sales_item_id = #{gachaItemId}) OR (T.type = 'EXCHANGE' AND TI.exchange_give_item_id = #{gachaItemId}) )
ORDER BY T.created_at DESC LIMIT #{pageSize} OFFSET #{offset}
