mapper.select:
WITH T AS (
    SELECT EXISTS (
        SELECT 1 FROM GACHA_WISH WHERE gacha_id = #{gachaId} AND user_id = #{userId}
    ) AS result
)
SELECT CASE WHEN result = 1 THEN 'Y' ELSE 'N' END AS wish_yn FROM T

mapper.insert:
INSERT INTO GACHA_WISH (
       user_id
     , gacha_id
     , created_at
)
VALUES (
       #{userId}
     , #{gachaId}
     , #{createdAt}
)
