mapper.select:
SELECT EXISTS (
    SELECT 1 FROM TRADE_SUGGEST
    WHERE trade_id         = #{tradeId}
     AND  trade_item_id    = #{tradeItemId}
     AND  user_id          = #{userId}
     AND  status          != 'DELETED'
     AND  purchase_item_id = #{purchaseItemId}
)

SELECT EXISTS (
    SELECT 1 FROM TRADE_SUGGEST
    WHERE trade_id                   = #{tradeId}
     AND  trade_item_id              = #{tradeItemId}
     AND  user_id                    = #{userId}
     AND  status                    != 'DELETED'
     AND  exchange_seller_item_id    = #{exchangeSellerItemId}
     AND  exchange_suggested_item_id = #{exchangeSuggestedItemId}
)

mapper.insert:
INSERT INTO TRADE_SUGGEST (
       id
     , user_id
     , trade_id
     , trade_item_id
     , gacha_id
     , type
     , status
     , content
     , created_at
     , updated_at
     , purchase_item_id
     , purchase_original_price
     , purchase_discount_yn
     , purchase_discount_price
     , exchange_seller_item_id
     , exchange_original_item_id
     , exchange_suggested_item_id
     , exchange_change_yn
)
VALUES (
       #{id}
     , #{userId}
     , #{tradeId}
     , #{tradeItemId}
     , #{gachaId}
     , #{type}
     , #{status}
     , #{content}
     , #{createdAt}
     , #{updatedAt}
     , #{purchaseItemId}
     , #{purchaseOriginalPrice}
     , #{purchaseDiscountYn}
     , #{purchaseDiscountPrice}
     , #{exchangeSellerItemId}
     , #{exchangeOriginalItemId}
     , #{exchangeSuggestedItemId}
     , #{exchangeChangeYn}
)

INSERT INTO CHAT_ROOM (
       id
     , trade_id
     , trade_item_id
     , trade_suggest_id
     , seller_id
     , buyer_id
     , status
     , created_at
)
VALUES (
       #{id}
     , #{tradeId}
     , #{tradeItemId}
     , #{tradeSuggestId}
     , #{sellerId}
     , #{buyerId}
     , #{status}
     , #{createdAt}
)
