mapper.select:
SELECT COUNT(id) FROM TRADE_PURCHASE_SUGGEST
WHERE user_id = #{userId}
AND trade_id = #{tradeId}
AND gacha_item_id = #{gachaItemId}
AND status != 'CANCELED'
AND status != 'REJECTED'

mapper.insert:
INSERT INTO TRADE_PURCHASE_SUGGEST (
       user_id
     , trade_id
     , gacha_id
     , gacha_item_id
     , content
     , original_price
     , count
     , discount_yn
     , discount_price
     , created_at
     , updated_at
)
VALUES (
       #{userId}
     , #{tradeId}
     , #{gachaId}
     , #{gachaItemId}
     , #{content}
     , #{originalPrice}
     , #{count}
     , #{discountYn}
     , #{discountPrice}
     , #{createdAt}
     , #{updatedAt}
)

mapper.select:
SELECT COUNT(id) FROM TRADE_EXCHANGE_SUGGEST
WHERE user_id = #{userId}
 AND trade_id = #{tradeId}
 AND suggested_item_id = #{suggestedItemId}
 AND status != 'CANCELED'
 AND status != 'REJECTED'

mapper.insert:
INSERT INTO TRADE_EXCHANGE_SUGGEST (
       user_id
     , trade_id
     , gacha_id
     , status
     , seller_item_id
     , change_yn
     , original_item_id
     , suggested_item_id
     , content
     , created_at
     , updated_at
)
VALUES (
       #{userId}
     , #{tradeId}
     , #{gachaId}
     , #{status}
     , #{sellerItemId}
     , #{changeYn}
     , #{originalItemId}
     , #{suggestedItemId}
     , #{content}
     , #{createdAt}
     , #{updatedAt}
)
