mapper.select:
SELECT EXISTS (
    SELECT 1 FROM TRADE
    WHERE gacha_id = #{gachaId}
     AND  user_id  = #{userId}
     AND  status   = 'PENDING'
     AND  type     = 'EXCHANGE'
)

SELECT EXISTS (
    SELECT 1 FROM TRADE
    WHERE gacha_id = #{gachaId}
     AND  user_id  = #{userId}
     AND  status   = 'PENDING'
     AND  type     = 'SALES'
)

mapper.insert:
INSERT INTO TRADE (
       id
     , gacha_id
     , user_id
     , type
     , status
     , title
     , content
     , file_id
     , hope_method
     , hope_location
     , hope_address
     , hope_location_latitude
     , hope_location_longitude
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{gachaId}
     , #{userId}
     , #{type}
     , #{status}
     , #{title}
     , #{content}
     , #{fileId}
     , #{hopeMethod}
     , #{hopeLocation}
     , #{hopeAddress}
     , #{hopeLocationLatitude}
     , #{hopeLocationLongitude}
     , #{createdAt}
     , #{updatedAt}
)

INSERT INTO TRADE_ITEM (
       id
     , trade_id
     , gacha_id
     , type
     , status
     , created_at
     , updated_at
     , sales_item_id
     , sales_count
     , sales_current_count
     , sales_price
     , exchange_give_item_id
     , exchange_want_item_id
)
VALUES (
       #{id}
     , #{tradeId}
     , #{gachaId}
     , #{type}
     , #{status}
     , #{createdAt}
     , #{updatedAt}
     , #{salesItemId}
     , #{salesCount}
     , #{salesCurrentCount}
     , #{salesPrice}
     , #{exchangeGiveItemId}
     , #{exchangeWantItemId}
)

mapper.update:
UPDATE TRADE
SET
       title                   = #{title}
     , content                 = #{content}
     , file_id                 = #{fileId}
     , hope_method             = #{hopeMethod}
     , hope_address            = #{hopeAddress}
     , hope_location_latitude  = #{hopeLocationLatitude}
     , hope_location_longitude = #{hopeLocationLongitude}
WHERE id = #{tradeId}

UPDATE TRADE_ITEM
SET
       give_item_id = #{giveItemId}
     , want_item_id = #{wantItemId}
WHERE id = #{tradeItemId}
