mapper.select:
SELECT EXISTS (
    SELECT 1 FROM TRADE
    WHERE gacha_id = #{gachaId}
     AND  user_id  = #{userId}
     AND  status   = 'PENDING'
     AND  type     = 'EXCHANGE'
)

SELECT EXISTS (
    SELECT 1 FROM TRADE
    WHERE gacha_id = #{gachaId}
     AND  user_id  = #{userId}
     AND  status   = 'PENDING'
     AND  type     = 'SALES'
)

mapper.insert:
INSERT INTO TRADE (
       id
     , gacha_id
     , user_id
     , type
     , status
     , title
     , content
     , file_id
     , hope_method
     , hope_location
     , hope_address
     , hope_location_latitude
     , hope_location_longitude
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{gachaId}
     , #{userId}
     , #{type}
     , #{status}
     , #{title}
     , #{content}
     , #{fileId}
     , #{hopeMethod}
     , #{hopeLocation}
     , #{hopeAddress}
     , #{hopeLocationLatitude}
     , #{hopeLocationLongitude}
     , #{createdAt}
     , #{updatedAt}
)

INSERT INTO TRADE_ITEM (
       id
     , trade_id
     , gacha_id
     , type
     , status
     , created_at
     , updated_at
     , sales_item_id
     , sales_count
     , sales_current_count
     , sales_price
     , exchange_give_item_id
     , exchange_want_item_id
)
VALUES (
       #{id}
     , #{tradeId}
     , #{gachaId}
     , #{type}
     , #{status}
     , #{createdAt}
     , #{updatedAt}
     , #{salesItemId}
     , #{salesCount}
     , #{salesCurrentCount}
     , #{salesPrice}
     , #{exchangeGiveItemId}
     , #{exchangeWantItemId}
)

mapper.select:
SELECT COUNT(T.id) FROM TRADE T WHERE T.gacha_id = #{gachaId}
AND T.status = 'PENDING'
AND EXISTS ( SELECT 1 FROM TRADE_ITEM TI WHERE TI.trade_id = T.id AND ( (T.type = 'SALES' AND TI.sales_item_id = #{gachaItemId}) OR (T.type = 'EXCHANGE' AND TI.exchange_give_item_id = #{gachaItemId}) ) )

SELECT T.id AS trade_id , T.gacha_id , T.user_id , U.nickname AS user_nickname , T.type , T.status , T.title , T.content , T.file_id , (SELECT file_url FROM IMAGE_FILE WHERE file_id = T.file_id ORDER BY created_at LIMIT 1) AS file_url , T.hope_method , T.hope_location , T.hope_address , T.hope_location_latitude , T.hope_location_longitude , (SELECT COUNT(id) FROM TRADE_SUGGEST WHERE trade_id = T.id AND gacha_id = T.gacha_id AND status != 'CANCELED') AS suggest_count , T.created_at FROM TRADE T INNER JOIN USER U ON U.id = T.user_id WHERE T.gacha_id = #{gachaId}
AND T.status = 'PENDING'
AND EXISTS ( SELECT 1 FROM TRADE_ITEM TI WHERE TI.trade_id = T.id AND ( (T.type = 'SALES' AND TI.sales_item_id = #{gachaItemId}) OR (T.type = 'EXCHANGE' AND TI.exchange_give_item_id = #{gachaItemId}) ) )
ORDER BY T.created_at DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE TRADE
SET
       title                   = #{request.title}
     , content                 = #{request.content}
     , file_id                 = #{request.fileId}
     , hope_method             = #{request.hopeMethod}
     , hope_location           = #{request.hopeLocation}
     , hope_address            = #{request.hopeAddress}
     , hope_location_latitude  = #{request.hopeLocationLatitude}
     , hope_location_longitude = #{request.hopeLocationLongitude}
     , updated_at              = CURRENT_TIMESTAMP
WHERE id = #{tradeId}

UPDATE TRADE_ITEM
SET
       exchange_give_item_id = #{request.giveItemId}
     , exchange_want_item_id = #{request.wantItemId}
     , updated_at            = CURRENT_TIMESTAMP
WHERE id = #{request.tradeItemId}
