mapper.select:
SELECT G.id AS gacha_id, G.title
FROM GACHA G
 INNER JOIN GACHA_WISH GW ON GW.gacha_id = G.id
WHERE GW.user_id = #{userId}
ORDER BY GW.created_at DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT id AS gacha_item_id, name
FROM GACHA_ITEM
WHERE gacha_id = #{gachaId}

SELECT G.id AS gacha_id, G.title FROM GACHA G WHERE 1=1
AND ( G.title LIKE CONCAT('%', #{keyword}, '%') OR G.manufacturer LIKE CONCAT('%', #{keyword}, '%') OR EXISTS ( SELECT 1 FROM GACHA_ITEM GI WHERE GI.gacha_id = G.id AND GI.name LIKE CONCAT('%', #{keyword}, '%') ) )
LIMIT #{pageSize} OFFSET #{offset}
