mapper.update:
UPDATE USER
SET
       file_id    = #{newFileId}
     , updated_at = CURRENT_TIMESTAMP
WHERE id = #{userId}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER WHERE nickname = #{nickname}
)

mapper.update:
UPDATE USER
SET
       nickname   = #{newNickname}
     , updated_at = CURRENT_TIMESTAMP
WHERE id = #{userId}

UPDATE USER
SET
       password   = #{newPassword}
     , updated_at = CURRENT_TIMESTAMP
WHERE id = #{userId}
