mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER WHERE phone_number = #{phoneNumber}
)

mapper.insert:
INSERT INTO USER_IDENTIFY_CODE (
       phone_number
     , code
     , type
     , name
     , ci
     , expired_at
)
VALUES (
       #{phoneNumber}
     , #{code}
     , #{type}
     , #{name}
     , #{ci}
     , #{expiredAt}
)

mapper.update:
UPDATE USER_IDENTIFY_CODE
SET
       name       = #{name}
     , ci         = #{ci}
     , expired_at = DATE_ADD(expired_at, INTERVAL 30 MINUTE)
WHERE phone_number = #{phoneNumber}
 AND  type         = #{type}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER_IDENTIFY_CODE
    WHERE phone_number = #{phoneNumber}
     AND  name         = #{name}
     AND  ci           = #{ci}
     AND  expired_at   > CURRENT_TIMESTAMP
)

SELECT EXISTS (
    SELECT 1 FROM USER WHERE email = #{email}
)

SELECT EXISTS (
    SELECT 1 FROM USER WHERE nickname = #{nickname}
)

mapper.insert:
INSERT INTO USER (
       id
     , role
     , status
     , email
     , password
     , name
     , nickname
     , phone_number
     , ci
     , file_id
     , refresh_token
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{role}
     , #{status}
     , #{email}
     , #{password}
     , #{name}
     , #{nickname}
     , #{phoneNumber}
     , #{ci}
     , #{fileId}
     , #{refreshToken}
     , #{lastLoginAt}
     , #{createdAt}
     , #{updatedAt}
)
