mapper.select:
SELECT EXISTS (
    SELECT 1 FROM TRADE_USER_COMPLIMENT
    WHERE compliment_user_id = #{userId}
     AND  trade_suggest_id   = #{tradeSuggestId}
)

mapper.insert:
INSERT INTO TRADE_USER_COMPLIMENT (
       trade_id
     , trade_item_id
     , trade_suggest_id
     , compliment_user_id
     , complimented_user_id
     , compliment_at
)
VALUES (
       #{tradeId}
     , #{tradeItemId}
     , #{tradeSuggestId}
     , #{complimentUserId}
     , #{complimentedUserId}
     , #{complimentAt}
)
