mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER WHERE nickname = #{nickname}
)

SELECT EXISTS (
    SELECT 1 FROM USER WHERE email = #{email}
)

mapper.insert:
INSERT INTO USER_IDENTIFY_CODE (
       email
     , code
     , type
     , expired_at
)
VALUES (
       #{email}
     , #{code}
     , #{type}
     , #{expiredAt}
)

INSERT INTO ITDA.USER (
       id
     , role
     , status
     , email
     , password
     , name
     , nickname
     , file_id
     , refresh_token
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{role}
     , #{status}
     , #{email}
     , #{password}
     , #{name}
     , #{nickname}
     , #{fileId}
     , #{refreshToken}
     , #{lastLoginAt}
     , #{createdAt}
     , #{updatedAt}
)

mapper.update:
UPDATE USER
SET
       refresh_token = #{rtk}
     , last_login_at = CURRENT_TIMESTAMP
     , updated_at    = CURRENT_TIMESTAMP
WHERE id = #{userId}

mapper.insert:
INSERT INTO USER_LOGIN_HISTORY (
