mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER_PLACE WHERE user_id = #{userId} AND name = #{placeName}
)

mapper.insert:
INSERT INTO USER_PLACE (
       id
     , user_id
     , latitude
     , longitude
     , address
     , name
     , is_default
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{userId}
     , #{latitude}
     , #{longitude}
     , #{address}
     , #{name}
     , #{isDefault}
     , #{createdAt}
     , #{updatedAt}
)
