mapper.select:
SELECT EXISTS (
    SELECT 1 FROM USER_IDENTIFY_CODE
    WHERE phone_number = #{phoneNumber}
     AND  type         = #{type}
     AND  name         = #{name}
     AND  ci           = #{ci}
     AND  expired_at   > CURRENT_TIMESTAMP
)

SELECT EXISTS (
    SELECT 1 FROM USER WHERE email = #{email}
)

SELECT EXISTS (
    SELECT 1 FROM USER WHERE nickname = #{nickname}
)

mapper.insert:
INSERT INTO USER (
       id
     , role
     , status
     , email
     , password
     , name
     , nickname
     , phone_number
     , ci
     , file_id
     , refresh_token
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{role}
     , #{status}
     , #{email}
     , #{password}
     , #{name}
     , #{nickname}
     , #{phoneNumber}
     , #{ci}
     , #{fileId}
     , #{refreshToken}
     , #{lastLoginAt}
     , #{createdAt}
     , #{updatedAt}
)
